import React, { useEffect, useState } from 'react';
import './App.css';
import Modal from './Modal';

function App() {
  const [products, setProducts] = useState([]);
  const [category, setCategory] = useState('');
  const [sortBy, setSortBy] = useState('');
  const [selectedProduct, setSelectedProduct] = useState(null);

  useEffect(() => {
    let url = 'http://localhost:5000/api/products';
    const params = [];
    if (category) params.push(`category=${category}`);
    if (sortBy) params.push(`sortBy=${sortBy}`);
    if (params.length) url += '?' + params.join('&');

    fetch(url)
      .then(res => res.json())
      .then(data => {
        const withRatings = data.map((p, i) => ({
          ...p,
          rating: p.rating || [4.2, 3.8, 5, 4.0][i % 4],
          review: p.review || [
            "Excellent quality!",
            "Good value for money.",
            "Highly recommended!",
            "Decent product."
          ][i % 4]
        }));
        setProducts(withRatings);
      });
  }, [category, sortBy]);

  const renderStars = (rating) => {
    const fullStars = Math.floor(rating);
    const half = rating - fullStars >= 0.5;
    const stars = "‚≠ê".repeat(fullStars) + (half ? "‚ú∞" : "");
    return stars;
  };

  return (
    <div className="App">
      <h1 className="heading">üõí Product Listing</h1>

      <div style={{ marginBottom: "20px", display: "flex", gap: "10px", justifyContent: "center" }}>
        <select onChange={(e) => setCategory(e.target.value)}>
          <option value="">All Categories</option>
          <option value="Electronics">Electronics</option>
          <option value="Clothing">Clothing</option>
          <option value="Footwear">Footwear</option>
          <option value="Furniture">Furniture</option>
        </select>
        <select onChange={(e) => setSortBy(e.target.value)}>
          <option value="">Sort By</option>
          <option value="asc">Price: Low to High</option>
          <option value="desc">Price: High to Low</option>
        </select>
      </div>

      <div className="product-list">
        {products.map((p, i) => (
          <div key={i} className="product" onClick={() => setSelectedProduct(p)}>
            <img
              src={`http://localhost:5000${p.image}`}
              alt={p.name}
              onError={(e) => (e.target.src = "https://via.placeholder.com/150")}
            />
            <h3>{p.name}</h3>
            <p>‚Çπ{p.price}</p>
            <p>{p.category}</p>
            <p>{renderStars(p.rating)} ({p.rating})</p>
          </div>
        ))}
      </div>

      <Modal show={selectedProduct !== null} onClose={() => setSelectedProduct(null)} product={selectedProduct} />
    </div>
  );
}

export default App;
