// Modal.js
import React from 'react';

const Modal = ({ show, onClose, product }) => {
  if (!show || !product) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="modal-close" onClick={onClose}>❌</button>
        <h2>{product.name}</h2>
        <img src={`http://localhost:5000${product.image}`} alt={product.name} />
        <p><strong>Price:</strong> ₹{product.price}</p>
        <p><strong>Category:</strong> {product.category}</p>
        <p><strong>Rating:</strong> {renderStars(product.rating)} ({product.rating})</p>
        <p><strong>Review:</strong> {product.review || "No reviews yet."}</p>
      </div>
    </div>
  );
};

const renderStars = (rating) => {
  const fullStars = Math.floor(rating);
  const half = rating - fullStars >= 0.5;
  const stars = "⭐".repeat(fullStars) + (half ? "✰" : "");
  return stars;
};

export default Modal;
